/* This file contains the generic mixin functions which can be used by all the base components */

/* Mixin function for adding the styles for disabled state of a component */

/* A string providing the css styles can be passed to this function as argument
to provide the overriding styles */
function includeDisabledState(css) {
  return "\n  &:disabled {\n      cursor: not-allowed;\n      ".concat(css && css, "\n  }\n  ");
}

export default {
  includeDisabledState: includeDisabledState
};